mapper.select:
select user.*, role.*, cu.*
from user,
     role,
     user_role ur,
     city_user cu
where user_email = #{value,jdbcType=VARCHAR}
   OR user_tel = #{value,jdbcType=VARCHAR}
    and user.user_id = ur.user_id
    and role.role_id = ur.role_id
    and user.user_id = cu.user_id

mapper.update:
update user
user_nick_name=#{userNickName,jdbcType=VARCHAR}, user_email=#{userEmail,jdbcType=VARCHAR}, user_tel=#{userTel,jdbcType=VARCHAR}, user_avatar=#{userAvatar,jdbcType=VARCHAR}, last_login_ip=#{lastLoginIp,jdbcType=VARCHAR}, last_login_time=#{lastLoginTime,jdbcType=TIMESTAMP},
where user_id=#{userId,jdbcType=INTEGER}

update user
set enabled=1
where user_id = #{id}
